import React, { useEffect, useState } from 'react';
import axios from 'axios';

function App() {
  const [blogs, setBlogs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [form, setForm] = useState({ title: '', content: '' });
  const [editing, setEditing] = useState(null);

  const API_URL = 'http://localhost:5000/api/blogs'; // Update to match your backend URL

  // Fetch blogs from the API
  useEffect(() => {
    const fetchBlogs = async () => {
      try {
        const response = await axios.get(API_URL);
        setBlogs(response.data);
        setLoading(false);
      } catch (err) {
        setError('Failed to fetch blogs');
        setLoading(false);
      }
    };

    fetchBlogs();
  }, []);

  // Handle form input changes
  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  // Create a new blog
  const handleCreate = async () => {
    try {
      const response = await axios.post(API_URL, form);
      setBlogs([...blogs, response.data]);
      setForm({ title: '', content: '' });
    } catch (err) {
      setError('Failed to create blog');
    }
  };

  // Edit an existing blog
  const handleEdit = (blog) => {
    setForm({ title: blog.title, content: blog.content });
    setEditing(blog._id);
  };

  // Update a blog
  const handleUpdate = async () => {
    try {
      const response = await axios.put(`${API_URL}/${editing}`, form);
      setBlogs(
        blogs.map((blog) =>
          blog._id === editing ? response.data : blog
        )
      );
      setForm({ title: '', content: '' });
      setEditing(null);
    } catch (err) {
      setError('Failed to update blog');
    }
  };

  // Delete a blog
  const handleDelete = async (id) => {
    try {
      await axios.delete(`${API_URL}/${id}`);
      setBlogs(blogs.filter((blog) => blog._id !== id));
    } catch (err) {
      setError('Failed to delete blog');
    }
  };

  if (loading) return <div>Loading...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div style={styles.container}>
      <h1 style={styles.heading}>Blogs</h1>

      {/* Form for Creating/Updating Blogs */}
      <div style={styles.form}>
        <input
          style={styles.input}
          type="text"
          name="title"
          placeholder="Blog Title"
          value={form.title}
          onChange={handleChange}
        />
        <textarea
          style={styles.textarea}
          name="content"
          placeholder="Blog Content"
          value={form.content}
          onChange={handleChange}
        ></textarea>
        <button
          style={styles.button}
          onClick={editing ? handleUpdate : handleCreate}
        >
          {editing ? 'Update Blog' : 'Create Blog'}
        </button>
      </div>

      {/* List of Blogs */}
      <div style={styles.blogList}>
        {blogs.map((blog) => (
          <div key={blog._id} style={styles.blogItem}>
            <h2 style={styles.title}>{blog.title}</h2>
            <p style={styles.content}>{blog.content}</p>
            <button style={styles.editButton} onClick={() => handleEdit(blog)}>
              Edit
            </button>
            <button
              style={styles.deleteButton}
              onClick={() => handleDelete(blog._id)}
            >
              Delete
            </button>
          </div>
        ))}
      </div>
    </div>
  );
}

const styles = {
  container: {
    fontFamily: 'Arial, sans-serif',
    padding: '20px',
    maxWidth: '800px',
    margin: '0 auto',
  },
  heading: {
    textAlign: 'center',
    color: '#333',
  },
  form: {
    marginBottom: '20px',
  },
  input: {
    display: 'block',
    width: '100%',
    padding: '10px',
    marginBottom: '10px',
    fontSize: '16px',
  },
  textarea: {
    display: 'block',
    width: '100%',
    padding: '10px',
    marginBottom: '10px',
    fontSize: '16px',
  },
  button: {
    padding: '10px 20px',
    fontSize: '16px',
    backgroundColor: '#28a745',
    color: '#fff',
    border: 'none',
    cursor: 'pointer',
  },
  blogList: {
    marginTop: '20px',
  },
  blogItem: {
    border: '1px solid #ddd',
    borderRadius: '5px',
    padding: '15px',
    marginBottom: '20px',
    background: '#f9f9f9',
  },
  title: {
    margin: '0 0 10px',
    color: '#555',
  },
  content: {
    color: '#666',
  },
  editButton: {
    marginRight: '10px',
    padding: '5px 10px',
    backgroundColor: '#ffc107',
    border: 'none',
    color: '#fff',
    cursor: 'pointer',
  },
  deleteButton: {
    padding: '5px 10px',
    backgroundColor: '#dc3545',
    border: 'none',
    color: '#fff',
    cursor: 'pointer',
  },
};

export default App;
