import React, { useEffect, useState } from 'react';
import axios from 'axios';

function App() {
  const [blogs, setBlogs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Fetch blogs from the API
  useEffect(() => {
    const fetchBlogs = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/blogs'); // Update with your backend URL
        setBlogs(response.data);
        setLoading(false);
      } catch (err) {
        setError('Failed to fetch blogs');
        setLoading(false);
      }
    };

    fetchBlogs();
  }, []);

  if (loading) return <div>Loading...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div style={styles.container}>
      <h1 style={styles.heading}>Blogs</h1>
      <div style={styles.blogList}>
        {blogs.map((blog) => (
          <div key={blog._id} style={styles.blogItem}>
            <h2 style={styles.title}>{blog.title}</h2>
            <p style={styles.content}>{blog.content}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

const styles = {
  container: {
    fontFamily: 'Arial, sans-serif',
    padding: '20px',
    maxWidth: '800px',
    margin: '0 auto',
  },
  heading: {
    textAlign: 'center',
    color: '#333',
  },
  blogList: {
    marginTop: '20px',
  },
  blogItem: {
    border: '1px solid #ddd',
    borderRadius: '5px',
    padding: '15px',
    marginBottom: '20px',
    background: '#f9f9f9',
  },
  title: {
    margin: '0 0 10px',
    color: '#555',
  },
  content: {
    color: '#666',
  },
};

export default App;
